/* *********************************************** Author :111qqz Created Time :2016年05月19日 星期四 16时52分03秒 File Name :code/hdu/3530.cpp ************************************************ */ #include &lt;cstdio&gt; #include &lt;cstring&gt; #include &lt;iostream&gt; #include &lt;algorithm&gt; #include &lt;vector&gt; #include &lt;queue&gt; #include &lt;set&gt; #include &lt;map&gt; #include &lt;string&gt; #include &lt;cmath&gt; #include &lt;cstdlib&gt; #include &lt;ctime&gt; #define fst first #define sec second #define lson l,m,rt&lt;&lt;1 #define rson m+1,r,rt&lt;&lt;1|1 #define ms(a,x) memset(a,x,sizeof(a)) typedef long long LL; #define pi pair &lt; int ,int &gt; #define MP make_pair using namespace std; const double eps = 1E-8; const int dx4[4]={1,0,0,-1}; const int dy4[4]={0,-1,1,0}; const int inf = 0x3f3f3f3f; const int N=1E5+7; int n; int a[N]; int dp[N][20],dp2[N][20]; int m,k; void rmq_init() { for ( int i = 1 ; i &lt;= n ; i ++) dp[i][0] = dp2[i][0] = a[i]; for ( int j = 1 ; (1&lt;&lt;j) &lt;= n ; j++) for ( int i = 1 ; i + (1&lt;&lt;j) -1 &lt;= n ; i++) { dp[i][j] = max(dp[i][j-1],dp[i+(1&lt;&lt;(j-1))][j-1]); dp2[i][j]=min(dp2[i][j-1],dp2[i+(1&lt;&lt;(j-1))][j-1]); } } int rmq( int l,int r) { int k = 0 ; while (1&lt;&lt;(k+1)&lt;=r-l+1) k++; int mx = max(dp[l][k],dp[r-(1&lt;&lt;k)+1][k]); int mn = min(dp2[l][k],dp2[r-(1&lt;&lt;k)+1][k]); return mx-mn; } int ruler() { int head = 1; int tail = 1; int res = -1 ; while (tail&lt;=n) { int cur = rmq(head,tail); while (head&lt;tail&amp;&amp;rmq(head,tail)&gt;k) head++; while (tail&lt;n&amp;&amp;rmq(head,tail)&lt;m) tail++; //if (tail&gt;n) break; cur = rmq(head,tail); if (cur&gt;=m&amp;&amp;cur&lt;=k) { res = max(res,tail-head); } // cout&lt;&lt;&quot;head:&quot;&lt;&lt;head&lt;&lt;&quot; tail:&quot;&lt;&lt;tail&lt;&lt;&quot; cur:&quot;&lt;&lt;cur&lt;&lt;&quot; res:&quot;&lt;&lt;res&lt;&lt;endl; tail++; } return res+1; } int main() { #ifndef ONLINE_JUDGE freopen(&quot;code/in.

